      editing: false,
      currentid: "",
      currentValue: "",
     
    };
  }

  feild = (e) => {
    this.setState({ value: e.target.value })
    
  }



  Addtask = () => {
    this.setState({ value: "" })
    const obj = {
      name: this.state.value,
      


    }

    if (this.state.value !== "") {
      this.setState({ Todoarry: this.state.Todoarry.concat(obj) })
      
    }

  }

  delete = (ItemId) => {
   
      const filterdata = this.state.Todoarry.filter(( i) => {
        return i.id !== ItemId
