import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { submit_btn } from '../Container/Services/Reducres/Todoreducer'

const Todo = () => {
    const [val, setval] = useState('')
    const dispatch = useDispatch()
    const myarr = useSelector((state) => state.custom.Mydata)


    const inputval = (e) => {
        setval(e.target.value)
    }
    const mybtn = () => {
        dispatch({
            type: "add",
            payload:val

        })
        console.log(val,'vla');
        setval('')

    }



    return (
        <div className="container">
            <h1>TodoList </h1>
            <input onChange={inputval} className='input' type="text" value={val} />
            <button onClick={mybtn} className='btn-primary'>Add task</button>
            {myarr.map((values, index) => {

                return (

                    <div key={index}><ul><li>{values}</li></ul></div>
                )
            })}
            ;
        </div>

    )

}

export default Todo
