import React from 'react'

class Class extends React.Component {

  constructor() {
    super();
    this.state = {
      Todoarry: [],
      value: "",
      edting:false,
    };
  }

  feild = (e) => {
    this.setState({ value: e.target.value })
    
  }



  Addtask = () => {
    this.setState({ value: "" })
    const obj = {
      name: this.state.value,
      


    }

    if (this.state.value !== "") {
      this.setState({ Todoarry: this.state.Todoarry.concat(obj) })
      
    }

  }

  delete = (ItemId) => {
    this.setState({
      Todoarry: [...this.state.Todoarry].filter((val, i) => {
        return i !== ItemId
      })

    })


  }

  edittask=(editeitem,va)=>{
// console.log(editeitem,'edit');


const usr= [...this.state.Todoarry].find((item)=>{
  return(
  item!==editeitem)
  
});
this.setState({ value: usr })
// this.setState({Todoarry:[...this.state.Todoarry]})

console.log(usr,'find')

  
}

  



  render() {

    const mylist = this.state.Todoarry.map((item, index) => {
      return (
        <li>{item.name} <button onClick={() => this.delete(index)}>del</button>
        
        <button onClick={()=>this.edittask(item,index)}>Edit</button>
        </li>)
    })


    return (<>

      <input onChange={this.feild} type="text" value={this.state.value} />

      <button onClick={() => this.Addtask()}>user data</button>

      <ul><li>{mylist}</li></ul>
    </>)
  }
}
export default Class
