const initialState = {
    list: [],
};
const Todoreducers = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO":return {
            ...state,
            list:[
                ...state.list,
                {
                    data:action.data,
                    id:action.id,
                },
            ],
        }; 
        case "DELETE_TODO":
        const todo = state.list.filter((todo)=>todo.id!==action.id)    
        return {
            ...state,
            list:todo,

            
        }
        default:return state;
    }
}
 export default Todoreducers;
