import { Button } from 'bootstrap'
import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Add_todo, del_todo, edit_todo } from '../Container/Services/Reducres/Todoreducer'

const Todo = () => {
    const [val, setval] = useState('')
    const [update, setUpdate] = useState(false)
    const dispatch = useDispatch()
    const myarr = useSelector((state) => state.todo.arry)
    //   console.log(val,'j');

    const inputval = (e) => {
        setval(e.target.value)

    }


    const submit_btn = () => {
        dispatch(Add_todo(val))
        setval('')
    }
    const Del_btn = (index) => {

        dispatch(del_todo(index))
    }
    const edit_btn = (item, index) => {


        setval(item)
        setUpdate(true)

    }

    const update_todo = (index) => {
       if (update) {
        setUpdate(submit_btn.id)
       }
        
       setval(myarr.splice(index.id,1,val))
    

    }

    return (
        <div className="container">
            <h1>TodoList </h1>
            <input onChange={inputval} className='input' type="text" value={val} />
            {!update ? <button onClick={submit_btn} className='btn-primary'>Add task</button> : <button className='btn-primary' onClick={(id) => update_todo(id)}>Update</button>}
            {myarr.map((item, index) => {
                return (
                    <ul><li key={index}>{item} <button onClick={() => Del_btn(index)} className='btn-danger'>Delete</button>
                        <button className='btn-danger' onClick={() => edit_btn(item, index)}>Edit</button>

                    </li></ul>
                )

            })}
        </div>

    )

}

export default Todo
