import { createSlice } from "@reduxjs/toolkit";

const  initialState={
    arry:[],
}

const Mytodo = createSlice({
    name:'Myarr',
    initialState,
    reducers:{
        submit_btn(state,action){
        state.add= action.payload
        },
    }
})

export const {submit_btn}=  Mytodo.actions  
export default Mytodo.reducer
