import React from 'react'

class Class extends React.Component {

  constructor() {
    super();
    this.state = {
      Todoarry: [],
      value: "",
    };
  }

  feild = (e) => {
    this.setState({ value: e.target.value })
    // console.log(e.target.value);

  }



  Addtask = () => {
    const obj = {
      name: this.state.value,
      // id: Date.now()


    }

    if (this.state.value !== "") {
      this.setState({ Todoarry: this.state.Todoarry.concat(obj) })
      this.setState({ value: "" })
    }

  }

  delete = (ItemId) => {
    // console.log(ItemId, 'del');

    this.state.Todoarry.filter((cval,i)=>{
     this.setState({
      Todoarry:[...this.state.Todoarry].filter((c,i)=>
      
      
      
i.id!==ItemId

      )
     })



    })
  }



  render() {

    const mylist = this.state.Todoarry.map((item, index) => {
      return (
        <li>{item.name} <button onClick={() => this.delete(index)}>del</button></li>)
    })


    return (<>

      <input onChange={this.feild} type="text" value={this.state.value} />

      <button onClick={() => this.Addtask()}>user data</button>

      <ul><li>{mylist}</li></ul>
    </>)
  }
}
export default Class
