import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    arry: [],
   

}

const Mytodo = createSlice({
    name: 'Mytodo',
    initialState: initialState,
    reducers: {
        Add_todo: (state, action) => {
            state.arry.push(action.payload)
        },
        del_todo: (state, action) => {
            const delitem = state.arry.filter((item, index) => {
                return index !== action.payload
            });
            state.arry = delitem;
        },
        edit_todo:(state,action)=>{
            console.log(action,"action");
            const a=state.arry.splice(action.payload.id,1,action.payload.val)
            // return a
            console.log(a,"aaaaaaa");
           return state.arry=a
        }
       
    }
})


export const { Add_todo, del_todo,edit_todo } = Mytodo.actions;
export default Mytodo.reducer;
