import React from 'react'
import { useDispatch } from 'react-redux';

const Home=()=> {
const value = 20;
const dispatch = useDispatch();


const incre = ()=>{
dispatch({
type:"increment"
})
    
}
const decre = ()=>{
    dispatch({
        type:"decrement"
        })
    
}
  return (
  <>
  <button onClick={incre}>INCREMENET</button>
  <h1>{value}</h1>
  
  <button onClick={decre}>DECREMENET</button>
  
  </>
  )
}

export default Home
